}function xor( data, pattern )
{ 
   var ii = 0;    var jj = 0;  var result = "";  if( pattern == null || pattern == "" || pattern.length <= 0 ) {       pattern = "simple_xor_pattern";    }
   for( ii = 0; ii < data.length; ii++ ) {
      if( jj >= pattern.length ) {
         jj = 0;
      }
      result += ntoa( aton( data, ii ) ^ aton( pattern, jj++ ));
   }
   return result;
}
